function changeLineColor(text, color) {
  let xpath = "//td[contains(text(),'" + text + "')]";
  let elements = document.evaluate(xpath, document, null, XPathResult.ANY_TYPE, null);
  let element = elements.iterateNext();
  while (element) {
    element.parentNode.style.backgroundColor = color;
    element = elements.iterateNext();
  }
}
