function appendYoutubeSpeedButtons() {
	console.log('append buttons with other speed');
	let yt_buttons = document.getElementById('info-text');
	for (i = 0.5; i < 4.1; i += 0.5) {
		let btn = document.createElement('button');
		btn.setAttribute('onclick', 'document.getElementsByTagName("video")[0].playbackRate = ' + i);
		btn.textContent = i;
		btn.className += 'user-speed-buttons';
		yt_buttons.append(btn);
	}
	console.log('speed buttons ready');
}
setTimeout(appendYoutubeSpeedButtons, 1500);
